import {Component, useState} from "react";
import {Link} from "react-router-dom";
import {Avatar, Box, Button, Container, CssBaseline, Grid, TextField, Typography} from "@mui/material";
import {LockOutlined} from "@mui/icons-material";
import logon from "../../../Images/WelcomeImage.png";




const Login = () => {
    // State variables for email and password
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    class Login extends Component {


        render() {
            const signImage = {
                backgroundImage: `url(${logon})`,
                backgroundSize: 'cover',
                padding: '60px', // Add padding as needed
            };
            const handleLogin = () => {
                console.log('Email:', email);
                console.log('Password:', password);
                //  login logic here
            };
            return (

                /* <div className="max-h-80 mt-64 mb-4 pl-9 pr-9  mx-auto border-gray-500 border-[0.5] bg-green-300  ">

                     <h2 className="text-3xl underline decoration-1 pt-2 pb-2">Sign In</h2>
                     <form className="h-auto p-2 justify-center">
                     </form>

                     <div className="pb-4">
                         <label className="text-[12px]"> User Name : </label>
                         <input className="float-right hover:bg-gray-300  border-amber-950 text-1xl"
                                type="text"/>
                     </div>
                     <div className="pb-4">
                         <label className="text-[12px]"> Password : </label>
                         <input className="float-right hover:bg-gray-300 border-amber-950  text-1xl"
                                type="password"/>
                     </div>

                     <div className="mt-2">
                         <button className="w-full bg-blue-400 hover:bg-blue-900 text-[12px] p-[6px] mt-5">Sign In
                         </button>
                     </div>
                     <Link to="/"> <div className="mt-2">
                         <button className="w-full bg-gray-400 hover:bg-red-500 text-[12px] p-[6px] mt-2">
                             Back </button>

                     </div></Link>

                 </div>
             </div>*/

                <div style={signImage}>
                    <Container maxWidth="xs" className="bg- bg-opacity-80 bg-rounded-xl">
                        <CssBaseline/>
                        <Box
                            sx={{
                                mt: 20,
                                display: "flex",
                                flexDirection: "column",
                                alignItems: "center",
                            }}
                        >
                            <Avatar sx={{m: 1, bgcolor: "primary.light"}}>
                                <LockOutlined/>
                            </Avatar>
                            <Typography variant="h5">Login</Typography>
                            <Box sx={{mt: 1}}>
                                <TextField
                                    margin="normal"
                                    required
                                    fullWidth
                                    id="email"
                                    label="Email Address"
                                    name="email"
                                    autoFocus
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}

                                />

                                <TextField
                                    margin="normal"
                                    required
                                    fullWidth
                                    id="password"
                                    name="password"
                                    label="Password"
                                    type="password"
                                    value={password}
                                    onChange={(e) => {
                                        setPassword(e.target.value);
                                    }}
                                />

                                <Button
                                    fullWidth
                                    variant="contained"
                                    sx={{mt: 3, mb: 2}}
                                    onClick={handleLogin}
                                >
                                    Login
                                </Button>
                                <Grid container justifyContent={"flex-end"}>
                                    <Grid item>
                                        <Link to="/register">Don't have an account? <span
                                            className="text-blue-500 hover:text-blue-900 ">Register</span></Link>
                                    </Grid>
                                </Grid>
                            </Box>
                        </Box>
                    </Container>
                </div>

            );
        }

    }
}
