import React, {Component} from "react";

import hero from "../../../Images/hero-bg.jpg"
import {Product} from "../../common/Products/Product";
import {queryHelpers} from "@testing-library/react";

export class Home extends Component {
    constructor(props: Readonly<{ }>) {
        super(props);
        this.state={ data:[] }
    }


    componentDidMount() {
        this.fetchData();
    }

    fetchData= async ()=>{
        try {
            const response =await fetch("./product-data.json");
            const jsonData =await response.json();
            this.setState({data:jsonData})
        }catch (error){
            console.log("Fetching Data Not Found!!",error)
        }
    }

    render() {

        // @ts-ignore
        const {data} = this.state;
        return (
            <div className="flex">

                <div className="flex mt-5 mb-5 flex-wrap justify-center
                items-center mx-auto ">


                    <div>
                        <h2 className="text-6xl font-bold text-center "> Welcome to
                            Chicago !</h2>
                        <img src={hero} alt="Main Image"/>
                    </div>

                    <div className="flex mt-5 flex-wrap justify-center items-center mx-auto">
                        {
                            data.map((product:any)=>(
                                <Product key={product.id} data={product}/>
                            ))
                        }
                    </div>


                </div>

            </div>
        );
    }
}
