import React, {Component} from "react";

import hero from "../../../Images/hero-bg.jpg"

export class Home extends Component {


    render() {

        const imageStyle = {
            position: 'relative',
            maxWidth: '100%', // Ensure the image is responsive
        };
        const textStyle = {
            position: 'absolute',
            bottom: '10px', // Adjust the distance from the bottom
            left: '50%', // Center the text horizontally
            transform: 'translateX(-50%)', // Center the text horizontally
            color: 'white',
            fontSize: '18px',
            fontFamily: 'Arial, sans-serif', // Specify your desired font
            fontWeight: 'bold',
            textShadow: '2px 2px 4px rgba(0, 0, 0, 0.7)', // Add a subtle text shadow
        };

        return (
            <div className="flex">

                <div className="flex mt-5 mb-5 flex-wrap justify-center
                items-center mx-auto ">
                    <div>
                        <h2 style={textStyle} className="text-6xl  font-bold text-center "> Welcome to
                            Chicago ! </h2>
                        <img src={hero} style={imageStyle} alt="Main Image"/>
                    </div>


                </div>

            </div>
        );
    }
}
